webpackJsonp([0x7823329a5933],{4165:function(e,t){e.exports={data:{challengeNode:{title:"Change the Color of Text",description:["Now let's change the color of some of our text.","We can do this by changing the <code>style</code> of your <code>h2</code> element.","The property that is responsible for the color of an element's text is the <code>color</code> style property.","Here's how you would set your <code>h2</code> element's text color to blue:",'<code>&#60;h2 style="color: blue;"&#62;CatPhotoApp&#60;/h2&#62;</code>',"Note that it is a good practice to end inline <code>style</code> declarations with a <code>;</code> .","<hr>","Change your <code>h2</code> element's style so that its text color is red."],challengeType:0,fields:{slug:"/responsive-web-design/basic-css/change-the-color-of-text",blockName:"Basic CSS",tests:[{text:"Your <code>h2</code> element should be red.",testString:'assert($("h2").css("color") === "rgb(255, 0, 0)", \'Your <code>h2</code> element should be red.\');'},{text:"Your <code>style</code> declaration should end with a <code>;</code> .",testString:"assert(code.match(/<h2\\s+style\\s*=\\s*(\\'|\")\\s*color\\s*:\\s*(?:rgb\\(\\s*255\\s*,\\s*0\\s*,\\s*0\\s*\\)|rgb\\(\\s*100%\\s*,\\s*0%\\s*,\\s*0%\\s*\\)|red|#ff0000|#f00|hsl\\(\\s*0\\s*,\\s*100%\\s*,\\s*50%\\s*\\))\\s*\\;(\\'|\")>\\s*CatPhotoApp\\s*<\\/h2>/),' Your <code>style</code> declaration should end with a <code>;</code> .');"}]},required:[],files:{indexhtml:{key:"indexhtml",ext:"html",name:"index",contents:'<h2>CatPhotoApp</h2>\n<main>\n  <p>Click here to view more <a href="#">cat photos</a>.</p>\n  \n  <a href="#"><img src="https://bit.ly/fcc-relaxing-cat" alt="A cute orange cat lying on its back."></a>\n  \n  <div>\n    <p>Things cats love:</p>\n    <ul>\n      <li>cat nip</li>\n      <li>laser pointers</li>\n      <li>lasagna</li>\n    </ul>\n    <p>Top 3 things cats hate:</p>\n    <ol>\n      <li>flea treatment</li>\n      <li>thunder</li>\n      <li>other cats</li>\n    </ol>\n  </div>\n  \n  <form action="/submit-cat-photo">\n    <label><input type="radio" name="indoor-outdoor" checked> Indoor</label>\n    <label><input type="radio" name="indoor-outdoor"> Outdoor</label><br>\n    <label><input type="checkbox" name="personality" checked> Loving</label>\n    <label><input type="checkbox" name="personality"> Lazy</label>\n    <label><input type="checkbox" name="personality"> Energetic</label><br>\n    <input type="text" placeholder="cat photo URL" required>\n    <button type="submit">Submit</button>\n  </form>\n</main>',head:"",tail:""},indexjs:null,indexjsx:null}}},pathContext:{challengeMeta:{introPath:"",template:null,required:[],nextChallengePath:"/responsive-web-design/basic-css/use-css-selectors-to-style-elements",id:"bad87fee1348bd9aedf08803"},slug:"/responsive-web-design/basic-css/change-the-color-of-text"}}}});
