webpackJsonp([0x5e715e3dcdcb],{4177:function(e,t){e.exports={data:{challengeNode:{title:"Set the Font Family of an Element",description:["You can set which font an element should use, by using the <code>font-family</code> property.","For example, if you wanted to set your <code>h2</code> element's font to <code>sans-serif</code>, you would use the following CSS:","<blockquote>h2 {<br>&nbsp;&nbsp;font-family: sans-serif;<br>}</blockquote>","<hr>","Make all of your <code>p</code> elements use the <code>monospace</code> font."],challengeType:0,fields:{slug:"/responsive-web-design/basic-css/set-the-font-family-of-an-element",blockName:"Basic CSS",tests:[{text:"Your <code>p</code> elements should use the font <code>monospace</code>.",testString:'assert($("p").not(".red-text").css("font-family").match(/monospace/i), \'Your <code>p</code> elements should use the font <code>monospace</code>.\');'}]},required:[],files:{indexhtml:{key:"indexhtml",ext:"html",name:"index",contents:'<style>\n  .red-text {\n    color: red;\n  }\n\n  p {\n    font-size: 16px;\n  }\n</style>\n\n<h2 class="red-text">CatPhotoApp</h2>\n<main>\n  <p class="red-text">Click here to view more <a href="#">cat photos</a>.</p>\n  \n  <a href="#"><img src="https://bit.ly/fcc-relaxing-cat" alt="A cute orange cat lying on its back."></a>\n  \n  <div>\n    <p>Things cats love:</p>\n    <ul>\n      <li>cat nip</li>\n      <li>laser pointers</li>\n      <li>lasagna</li>\n    </ul>\n    <p>Top 3 things cats hate:</p>\n    <ol>\n      <li>flea treatment</li>\n      <li>thunder</li>\n      <li>other cats</li>\n    </ol>\n  </div>\n  \n  <form action="/submit-cat-photo">\n    <label><input type="radio" name="indoor-outdoor" checked> Indoor</label>\n    <label><input type="radio" name="indoor-outdoor"> Outdoor</label><br>\n    <label><input type="checkbox" name="personality" checked> Loving</label>\n    <label><input type="checkbox" name="personality"> Lazy</label>\n    <label><input type="checkbox" name="personality"> Energetic</label><br>\n    <input type="text" placeholder="cat photo URL" required>\n    <button type="submit">Submit</button>\n  </form>\n</main>',head:"",tail:""},indexjs:null,indexjsx:null}}},pathContext:{challengeMeta:{introPath:"",template:null,required:[],nextChallengePath:"/responsive-web-design/basic-css/import-a-google-font",id:"bad87fee1348bd9aede08807"},slug:"/responsive-web-design/basic-css/set-the-font-family-of-an-element"}}}});
